CustomField.formula:
IF(
    NOT(ISBLANK(Ticket_Finalizado_Em__c)),

    /* Quando finalizado */
    IF(FLOOR(Ticket_Finalizado_Em__c - Ticket_Criado_Em__c) > 0,
        TEXT(FLOOR(Ticket_Finalizado_Em__c - Ticket_Criado_Em__c)) & " dias, ",
        ""
    ) &
    IF(FLOOR(MOD((Ticket_Finalizado_Em__c - Ticket_Criado_Em__c) * 24, 24)) > 0,
        TEXT(FLOOR(MOD((Ticket_Finalizado_Em__c - Ticket_Criado_Em__c) * 24, 24))) & " horas, ",
        ""
    ) &
    TEXT(FLOOR(MOD((Ticket_Finalizado_Em__c - Ticket_Criado_Em__c) * 24 * 60, 60))) & " minutos",

    /* Quando ainda aberto */
    IF(FLOOR(TODAY() - DATEVALUE(Ticket_Criado_Em__c)) > 0,
        TEXT(FLOOR(TODAY() - DATEVALUE(Ticket_Criado_Em__c))) & " dias, ",
        ""
    ) &
    IF(FLOOR(MOD((NOW() - Ticket_Criado_Em__c) * 24, 24)) > 0,
        TEXT(FLOOR(MOD((NOW() - Ticket_Criado_Em__c) * 24, 24))) & " horas, ",
        ""
    ) &
    TEXT(FLOOR(MOD((NOW() - Ticket_Criado_Em__c) * 24 * 60, 60))) & " minutos"
)
