ValidationRule.errorConditionFormula:
AND(
    !$User.Ignorar_erros__c,
    OR(
        AND(
            ISNEW(),
            Avaliacao_da_demo_assinada__c = true
        ),
        AND(
            PRIORVALUE(Avaliacao_da_demo_assinada__c) != Avaliacao_da_demo_assinada__c,
            TEXT(Status__c) != 'Retorno da demo'
        )
    )
)
