ValidationRule.errorConditionFormula:
AND(
    !$User.Ignorar_erros__c,
    OR(
        AND(
            ISNEW(),
            Checklist_assinado__c = true
        ),
        AND(
            PRIORVALUE(Checklist_assinado__c ) != Checklist_assinado__c ,
            TEXT(Status__c) != 'Retorno da demo'
        )
    )
)
