ValidationRule.errorConditionFormula:
AND(
    !$User.Ignorar_erros__c,
    OR(
        AND(
            ISNEW(),
            Nota_de_retorno_assinada__c = true
        ),
        AND(
            PRIORVALUE(Nota_de_retorno_assinada__c ) != Nota_de_retorno_assinada__c ,
            TEXT(Status__c) != 'Retorno da demo'
        )
    )
)
