ISNEW(),
    NOT(
        OR(
            Contrato_de_compra__r.RecordType.DeveloperName = 'Representacao_Comercial',
            TEXT(Contrato_de_compra__r.Tipo_de_dura_o__c) = 'Indeterminado'
        )
    ),
    Valor_da_nota_fiscal__c > Contrato_de_compra__r.Saldo__c
)
