CustomField.formula:
IF(
    NOT(ISBLANK(Inicio_Faturado__c)),
    TEXT(
        FLOOR(
            (Inicio_Faturado__c - Inicio_Ag_Faturamento__c) -
            (FLOOR((Inicio_Faturado__c - Inicio_Ag_Faturamento__c) / 7) * 2) -
            IF(
                MOD(DATEVALUE(Inicio_Ag_Faturamento__c) - DATE(1900, 1, 7), 7) > 
                MOD(DATEVALUE(Inicio_Faturado__c) - DATE(1900, 1, 7), 7), 
                2, 
                0
            )
        )
    ) & ' dias e ' & 
    TEXT(
        FLOOR(
            (
                (Inicio_Faturado__c - Inicio_Ag_Faturamento__c - 
                FLOOR(Inicio_Faturado__c - Inicio_Ag_Faturamento__c)) * 24
            )
        )
    ) & ' horas',
    TEXT(
        FLOOR(
            (NOW() - Inicio_Ag_Faturamento__c) -
            (FLOOR((NOW() - Inicio_Ag_Faturamento__c) / 7) * 2) -
            IF(
                MOD(DATEVALUE(Inicio_Ag_Faturamento__c) - DATE(1900, 1, 7), 7) > 
                MOD(TODAY() - DATE(1900, 1, 7), 7), 
                2, 
                0
            )
        )
    ) & ' dias e ' & 
    TEXT(
        FLOOR(
            (
                (NOW() - Inicio_Ag_Faturamento__c - 
                FLOOR(NOW() - Inicio_Ag_Faturamento__c)) * 24
            )
        )
    ) & ' horas'
)
