CustomField.formula:
IF(TEXT(Tipo_de_consumo__c) == 'Prestação de serviços', 'Serviço',
    IF(TEXT(Tipo__c) == 'Compra para consumo', 'Compra para consumo',
        IF(TEXT(Tipo__c) == 'Compra para revenda OPME', 'Compra para revenda',
            IF(TEXT(Tipo__c) == 'Compra para revenda', 'Compra para revenda', 'Compra para revenda')
        )
    )
)
